% -- Degrees of Freedom --
Veh.DOF(1).name = 'yB1'; Veh.DOF(1).type = 'displacement'; Veh.DOF(1).dependency = 'independent';
Veh.DOF(2).name = 'thetaB1'; Veh.DOF(2).type = 'rotation'; Veh.DOF(2).dependency = 'independent';
Veh.DOF(3).name = 'thetaB2'; Veh.DOF(3).type = 'rotation'; Veh.DOF(3).dependency = 'independent';
Veh.DOF(4).name = 'yS1'; Veh.DOF(4).type = 'displacement'; Veh.DOF(4).dependency = 'independent';
Veh.DOF(5).name = 'yS2'; Veh.DOF(5).type = 'displacement'; Veh.DOF(5).dependency = 'independent';
Veh.DOF(6).name = 'yS3'; Veh.DOF(6).type = 'displacement'; Veh.DOF(6).dependency = 'independent';
Veh.DOF(7).name = 'yS4'; Veh.DOF(7).type = 'displacement'; Veh.DOF(7).dependency = 'independent';
Veh.DOF(8).name = 'yS5'; Veh.DOF(8).type = 'displacement'; Veh.DOF(8).dependency = 'independent';
