[Veh.Prop.afBi(2)*Veh.Prop.mBi(2), Veh.Prop.abBi(1)*Veh.Prop.afBi(2)*Veh.Prop.mBi(2), Veh.Prop.IyBi(2) + Veh.Prop.afBi(2)^2*Veh.Prop.mBi(2), 0, 0, 0, 0, 0]; ...
[0, 0, 0, Veh.Prop.mSi(1), 0, 0, 0, 0]; ...
[0, 0, 0, 0, Veh.Prop.mSi(2), 0, 0, 0]; ...
[0, 0, 0, 0, 0, Veh.Prop.mSi(3), 0, 0]; ...
[0, 0, 0, 0, 0, 0, Veh.Prop.mSi(4), 0]; ...
[0, 0, 0, 0, 0, 0, 0, Veh.Prop.mSi(5)]];
 
