[Veh.Prop.abBi(1)*Veh.Prop.kSi(3) + Veh.Prop.abBi(1)*Veh.Prop.kSi(4) + Veh.Prop.abBi(1)*Veh.Prop.kSi(5) + Veh.Prop.ei(1)*Veh.Prop.kSi(1) + Veh.Prop.ei(2)*Veh.Prop.kSi(2), Veh.Prop.abBi(1)^2*Veh.Prop.kSi(3) + Veh.Prop.abBi(1)^2*Veh.Prop.kSi(4) + Veh.Prop.abBi(1)^2*Veh.Prop.kSi(5) + Veh.Prop.ei(1)^2*Veh.Prop.kSi(1) + Veh.Prop.ei(2)^2*Veh.Prop.kSi(2), Veh.Prop.abBi(1)*Veh.Prop.afBi(2)*Veh.Prop.kSi(3) + Veh.Prop.abBi(1)*Veh.Prop.afBi(2)*Veh.Prop.kSi(4) + Veh.Prop.abBi(1)*Veh.Prop.afBi(2)*Veh.Prop.kSi(5) + Veh.Prop.abBi(1)*Veh.Prop.ei(3)*Veh.Prop.kSi(3) + Veh.Prop.abBi(1)*Veh.Prop.ei(4)*Veh.Prop.kSi(4) + Veh.Prop.abBi(1)*Veh.Prop.ei(5)*Veh.Prop.kSi(5), -Veh.Prop.ei(1)*Veh.Prop.kSi(1), -Veh.Prop.ei(2)*Veh.Prop.kSi(2), -Veh.Prop.abBi(1)*Veh.Prop.kSi(3), -Veh.Prop.abBi(1)*Veh.Prop.kSi(4), -Veh.Prop.abBi(1)*Veh.Prop.kSi(5)]; ...
[Veh.Prop.afBi(2)*Veh.Prop.kSi(3) + Veh.Prop.afBi(2)*Veh.Prop.kSi(4) + Veh.Prop.afBi(2)*Veh.Prop.kSi(5) + Veh.Prop.ei(3)*Veh.Prop.kSi(3) + Veh.Prop.ei(4)*Veh.Prop.kSi(4) + Veh.Prop.ei(5)*Veh.Prop.kSi(5), Veh.Prop.abBi(1)*Veh.Prop.afBi(2)*Veh.Prop.kSi(3) + Veh.Prop.abBi(1)*Veh.Prop.afBi(2)*Veh.Prop.kSi(4) + Veh.Prop.abBi(1)*Veh.Prop.afBi(2)*Veh.Prop.kSi(5) + Veh.Prop.abBi(1)*Veh.Prop.ei(3)*Veh.Prop.kSi(3) + Veh.Prop.abBi(1)*Veh.Prop.ei(4)*Veh.Prop.kSi(4) + Veh.Prop.abBi(1)*Veh.Prop.ei(5)*Veh.Prop.kSi(5), Veh.Prop.afBi(2)^2*Veh.Prop.kSi(3) + Veh.Prop.afBi(2)^2*Veh.Prop.kSi(4) + Veh.Prop.afBi(2)^2*Veh.Prop.kSi(5) + Veh.Prop.ei(3)^2*Veh.Prop.kSi(3) + Veh.Prop.ei(4)^2*Veh.Prop.kSi(4) + Veh.Prop.ei(5)^2*Veh.Prop.kSi(5) + 2*Veh.Prop.afBi(2)*Veh.Prop.ei(3)*Veh.Prop.kSi(3) + 2*Veh.Prop.afBi(2)*Veh.Prop.ei(4)*Veh.Prop.kSi(4) + 2*Veh.Prop.afBi(2)*Veh.Prop.ei(5)*Veh.Prop.kSi(5), 0, 0, - Veh.Prop.afBi(2)*Veh.Prop.kSi(3) - Veh.Prop.ei(3)*Veh.Prop.kSi(3), - Veh.Prop.afBi(2)*Veh.Prop.kSi(4) - Veh.Prop.ei(4)*Veh.Prop.kSi(4), - Veh.Prop.afBi(2)*Veh.Prop.kSi(5) - Veh.Prop.ei(5)*Veh.Prop.kSi(5)]; ...
[-Veh.Prop.kSi(1), -Veh.Prop.ei(1)*Veh.Prop.kSi(1), 0, Veh.Prop.kSi(1) + Veh.Prop.kTi(1), 0, 0, 0, 0]; ...
[-Veh.Prop.kSi(2), -Veh.Prop.ei(2)*Veh.Prop.kSi(2), 0, 0, Veh.Prop.kSi(2) + Veh.Prop.kTi(2), 0, 0, 0]; ...
[-Veh.Prop.kSi(3), -Veh.Prop.abBi(1)*Veh.Prop.kSi(3), - Veh.Prop.afBi(2)*Veh.Prop.kSi(3) - Veh.Prop.ei(3)*Veh.Prop.kSi(3), 0, 0, Veh.Prop.kSi(3) + Veh.Prop.kTi(3), 0, 0]; ...
[-Veh.Prop.kSi(4), -Veh.Prop.abBi(1)*Veh.Prop.kSi(4), - Veh.Prop.afBi(2)*Veh.Prop.kSi(4) - Veh.Prop.ei(4)*Veh.Prop.kSi(4), 0, 0, 0, Veh.Prop.kSi(4) + Veh.Prop.kTi(4), 0]; ...
[-Veh.Prop.kSi(5), -Veh.Prop.abBi(1)*Veh.Prop.kSi(5), - Veh.Prop.afBi(2)*Veh.Prop.kSi(5) - Veh.Prop.ei(5)*Veh.Prop.kSi(5), 0, 0, 0, 0, Veh.Prop.kSi(5) + Veh.Prop.kTi(5)]];
 
% -- Force vector to calculate static response --
% Note: When using this vector, multiply it by the gravity. Following the sign criteria defined here
%   gravity has negative value. The numerical value to use is grav = -9.81 m/s^2
Veh.Static.F_vector_no_grav = [Veh.Prop.mBi(1) + Veh.Prop.mBi(2), Veh.Prop.abBi(1)*Veh.Prop.mBi(2), Veh.Prop.afBi(2)*Veh.Prop.mBi(2), Veh.Prop.mSi(1), Veh.Prop.mSi(2), Veh.Prop.mSi(3), Veh.Prop.mSi(4), Veh.Prop.mSi(5)];
 
